tutorialsIndex_pt = {"Básico", "Intermediário", "Avançado", "Profissões", "Habilidades", "Extra", "Torneios", "Quests"}

tutorialsInfo_pt = {
    {
        {name = "01. Introdução", text = "1. O Menu Pokemon é onde você chama o seu Pokemon e verifica o seu estado e skills.\n\n2. Sua mochila é onde você irá armazenar seus objetos e seus Pokemons.\n\n3. Informações de seu char, como nível e experiência.", img = "imgs/b1"}, 
        {name = "02. Controlar Objetos", text = "Para controlar itens , basta clicar e segurar o botão esquerdo do pressionado o mouse e arrastá-lo para onde quiser.", img = "imgs/b2"}, 
        {name = "03. Chame seu Pokemon", text = "Para chamar seu Pokemon , Pokemon arraste -o para o menu, em seguida, clique no botão direito do seu mouse .", img = "imgs/b3"}, 
        {name = "04. Atacando Inimigos", text = "Para atacar um inimigo , basta clicar sobre ele com o botão direito do mouse, então o alvo é marcado e seus Pokémons atacarão o inimigo automaticamente.", img = "imgs/b4"},
        {name = "05. Usando Skill", text = "Cada skill de seu Pokemon é desbloqueada como você upa seu level e de seu Pokemon. Para usar uma skill basta clicar em seu ícone ( botão esquerdo do mouse ) ou usar atalhos (F1, F2 , F3, F4... ) atalhos também podem ser redefinidos nas hotkeys.", img = "imgs/b5"},
        {name = "06. Loots", text = "Todos os Pokemons depois de serem derrotados podem ter drops. Para coletá-los basta clicar sobre o Pokémon derrotado com o botão direito do mouse, depois você deve arrastá-los para sua mochila.", img = "imgs/b6"}, 
        {name = "07. Catchs de Pokemons", text = "Para capturar um Pokémon , clique em seus Pokeballs vazios ( botão direito do mouse ) e , em seguida, utilizados em um pokemon derrotado ( botão esquerdo do mouse ) .", img = "imgs/b7"}, 
        {name = "08. Mapa", text = "O mapa do jogo pode ser aberto com o atalho (Ctrl + M) ou seu ícone.", img = "imgs/b10"},
	{name = "09. Lojas", text = "Para abrir as ofertas de um NPC, diga (hi/trade) à ele.", img = "imgs/b11"},        
        {name = "10. Pokedex", text = "Para desbloquear novos dados dos Pokemons selvagens ou de outros treinadores, basta clicar no ícone e em seguida, usarno Pokedex em Pokemon que você deseja coletar informações.", img = "imgs/b12"},
        {name = "11. Depot", text = "O depot pode ser encontrado em qualquer Centro Pokémon, para abri-lo e clique no botão direito do mouse. No depot você pode manter o maior número de Pokemon e objetos que você deseja .", img = "imgs/b13"},
        {name = "12. Escadas e Cordas", text = "Escadas ou cordas de escalada basta clicar no botão direito do seu mouse na base da escada ou corda como a imagem indicando.", img = "imgs/b9"},
        {name = "13. Healar o Pokemon", text = "Para healar o seus Pokemons feridos, basta ir a um Centro Pokémon e dizer (hi) para a Nurse Joy, lembrando que não precisa pagar.", img = "imgs/b8"},
    },
    {
        {name = "01. Ver Objetos", text = "Para ter mais informações sobre o objeto, posicione o mouse sobre ele e pressione 2 botões simultaneamente.", img = "imgs/a1"},
        {name = "02. Trocar Outfit", text = "Para alterar sua roupa segure a tecla (Ctrl) e clique em seu personagem com o botão direito do mouse após seleções (Set Outfit).", img = "imgs/a2"},
        {name = "03. Pesca", text = "Para pescar ir para a tela superior próxima ao ícone da Pokedex e clique nele e depois na água.", img = "imgs/a3"},
        {name = "04. Felicidade do Pokemon", text = "Com o comando !love melhora o humor de seu Pokemon mas tem tempo determinado para usá-lo.", img = "imgs/a4"},
        {name = "05. Instâncias de Batalha", text = "Instâncias de batalha são muito importantes, principalmente para duelos.\n\nOfensivo: 10% de ataque extra e 10% à menos de defesa.\n\nEquilibrado: Força normal do Pokemon.\n\nDefensivo: 10 % de defesa extra e 10% à menos de ataque.", img = "imgs/a5"},
        {name = "06. Hotkeys", text = "Aqui você pode adicionar os atalhos como: remédios e poções ou comandos com os movimentos da batalha de seu Pokemon.", img = "imgs/a6"},
        {name = "07. Aceitando Quests", text = "Para aceitar uma quest, você precisa falar com o NPC que está necessitando de sua ajuda, dizendo: (Hi/help/yes).", img = "imgs/a7"},
        {name = "08. Tasks", text = "As Tasks diarias podem se encontrar no TC com varios NPCs >> Npcs\n\n(Leiko: Nivel Iniciante)\n\n(Miyuki: Nivel Facil)\n\n(Amaya: Nivel Medio)\n\n(Junko: Nivel Dificil).", img = "imgs/a8"},
        {name = "09. Batalhando com NPC", text = "É só dizer (hi/battle/yes).", img = "imgs/a9"},
        {name = "10. Viajando", text = "Para Viajar com determinado NPC basta dizer (hi/travel/yes).", img = "imgs/a10"},
        {name = "11. Evolução", text = "Para um Pokémon evoluir, ele precisa ter determinado level ou você pode usar pedras de evolução em determinados Pokémons, basta consultar na Pokédex.", img = "imgs/a11"},
        {name = "12. VIP List", text = "A lista de amigos mostra se seus amigos estão online ou offline no jogo, além de fornecer várias opções como mensagens privadas ou ignorar. Para adicionar um amigo, segure CTRL e clique com o botão direito do mouse na lista de amigos e escolha a opção Adicionar nova VIP.", img = "imgs/a12"},
    },	
    {
        {name = "01. Trade", text = "Para fazer uma troca, segure (Ctrl) e clique com o botão direito do mouse sobre o objeto que você deseja alterar e selecione (Trade with) e selecione o player que você quer fazer a troca. (Se for vários objetos, coloque-os em uma bag)", img = "imgs/c1"},
        {name = "02. House", text = "Para comprar uma house, você precisa ser Premium Account e estar no nível 30+.\n\nPara comprar uma casa, ela precisa estar sem dono, veja o preço da casa dando look em sua porta, depois só ir em frente a porta da house e dizer !buyhouse\n\nA cada mês será cobrado o preço da house, em seu depot, caso contrário, você vai perder a sua house.", img = "imgs/c2"},
        {name = "03. Batalha", text = "Batalha clique no botão na parte superior da tela e selecione outro jogador deste sistema\n\n1 Vs 1\n\2 Vs 2\n\3 Vs 3\n\4 Vs 4", img = "imgs/c3"},
        {name = "04. Channels", text = "1.Private Chat Channel (chat privado).\n\2.Game-Chat (conversa entre todos os players do servidor).\n\3.Trade Center (Channel de trocas e vendas de itens e Pokemons).\n\4.Help (Channel de ajuda aos Players).", img = "imgs/c4"},
        {name = "05. Guild", text = "A Guild só pode ser criada por um jogador de nível 80+ e exige dois vice-leaders. Para criar ou entrar em uma Guild basta clicar sobre o botão Guild e selecione a opção desejada e siga.", img = "imgs/c5"},
        {name = "06. TV", text = "Indisponível".},
        {name = "07. Mover Pokemon", text = "Alterar a direção de um Pokémon é útil para duelos e caças. Basta dizer suas teclas de atalho ou lugar nos comandos a seguir:\n\nNorte:t1\n\Este:t2\n\Sul:t3\n\Oeste:t4", img = "imgs/c7"},
        {name = "08. Clans", text = "A partir do level 80, os players são aceitos nos Clans. Divididos de acordo com os elementos, os players de Clans começam no Rank 1 e podem chegar ao Rank 5, seguindo a hierarquia dos clãs.\n\nPara ganhar a posição dentro de seu clã, você terá que fazer Tasks para subir seu Rank. Para entrar em um clã, converse com o líder que está na área do Clã, que se encontra no TC.\n\nTodo clã têm seus bônus para seus elementos, além de ter um Pokemon Shiny exclusivo, que pode ser adquirido por um clã NPC:\n\nNaturia: Grass/Bug - Pokemon: Shiny Vileplume.\n\Volcanic: Fire - Pokemon: Shiny Flareon.\n\Raibolt: Electric - Pokemon: Shiny Jolteon.\n\Seavell: Water/Ice - Pokemon: Shiny Vaporeon.\n\Wingeon: Dragon/Flying - Pokemon: Shiny Fearow.\n\Orebound: Rock/Ground - Pokemon: Shiny Golem.\n\Psycraft: Psychic/Fairy - Pokemon: Shiny Hypno.\n\Malefic: Ghost/Poison/Dark - Pokemon: Shiny Nidoking.\n\Ironhard: Metal/Crystal - Pokemon: Shiny Onix.", img = "imgs/c9"},
        {name = "09. Trocando Tokens", text = "Para alterar os sinais de exigência não é necessário ir Shamouti, em duelista Irmandade salão que fica a oeste do Pokemon Center\n\nUse a (fábrica de Held Item) e selecionar os Tokens de mudar.", img = "imgs/c8"},
        {name = "10. Party Channel", text = "Partido fazendo com alguém apenas dar (Ctrl + Clique) por outro jogador e clique (Partido Convidando).", img = "imgs/c10"},
    },	
    {
        {name = "01. Estilista", text = "To choose your profession, go to the NPC of your chosen profession and tell:\n\n-Hi.\n\n-Help.\n\n-Yes.", img = "imgs/p1"},
        {name = "02. Engenheiro", text = "To train professional constructor, go to the NPC Constructor Tutor and tell:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Open Table construction by clicking the right mouse button on it or by pressing Ctrl + right button and then choosing the option open, place the material you want to make the specific item you want, you might need one in your backpack Toolkit , then click again right on the table, and then click Work.\n\nObs: The constructor's instructions will be in the guide, Manual – Constructor.", img = "imgs/p2"},
        {name = "03. Professor", text = "To train professional doctor, go to the NPC Doctor and tell:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Open the bottle of potions by clicking the right mouse button on it or by pressing Ctrl + right button and then choosing the option open, put the ingredient you want to make the specific item you want, then click again right on the bottle and then click Work.\n\nObs: The doctor's instructions will be in the guide, Recipes – Doctor.", img = "imgs/p3"},
        {name = "04. Adventurer", text = "To train professional cooker, go to the NPC Cooking Tutor and tell:\n\n-Hi.\n\n-Enter.\n\n-Yes.\n\n- Open the stove click on the right mouse button on it or by pressing Ctrl + right button and then choosing the option open, put the ingredient you want to make the recipe specifies that you want, then click again right on the stove and then click Work.\n\nObs: The cooker's instructions will be in the guide, Recipes – Cooker.", img = "imgs/p4"},
    },	
    {
        {name = "01. Mover", text = "Para mover o seu Pokemon , abra a janela e clique no ícone ( Order) e em seguida, clique no chão, sua raiva Pokemon para o local escolhido.\n\nEste comando é importância vitar, duelos seniores assim recomendado o uso de um atalho para isso. A ordenação de atalho padrão.", img = "imgs/f1"},
        {name = "02. Corte", text = "Para cortar arbustos, apenas a ordena um Pokemon com corte habilidar no mato.", img = "imgs/f2"}, 
        {name = "04. Dig", text = "Abrir fechado para buracos, apenas a ordena um pokemon com a capacidade buracos Dig fechado.", img = "imgs/f3"},
        {name = "05. Light", text = "Para usar a luz, apenas a ordena um Pokémon com a própria Luz habilidade.", img = "imgs/f4"},
        {name = "06. Rock Smash", text = "Para pedras Quebar, apenas a ordena um Pokemon com Rock Smash habilidade em pedra.", img = "imgs/f5"},
        {name = "07. Headbutt", text = "Só encomendar um Pokemon com as árvores capacidade Headbutt para encontrar selvagem Pokemon.", img = "imgs/f6"},
        {name = "08. Ride", text = "Para montar um Pokemon, Pokemon com apenas pedir uma carona em seu treinador.\n\nPara remover repetir o mesmo processo.", img = "imgs/f7"},
        {name = "09. Surf", text = "Para nadar, apenas pedir um Pokémon com Surf, à beira de um lugar com água, em seguida, caminhar para o seu Pokemon.\n\nPara sair, repita o mesmo processo.", img = "imgs/f8"},
        {name = "10. Fly", text = "Para voar em um Pokemon, apenas pedir um Pokemon com a mosca no treinador.\n\nPara alterar a altura, use os comandos (H1) para aumentar e (h2) para diminuir.\n\nPara remover, repetir o mesmo processo.", img = "imgs/f9"},
        {name = "11. Teleport", text = "Se teletransportar para uma cidade, você precisa de um Pokémon com Teleport. e digite o comando (h`cerulean ou nome da cidade).", img = "imgs/f10"},
        {name = "12. Blink", text = "Quando um Pokémon tem Blink e você pedir -lhe para ir para algum lugar, a usar automaticamente o piscar.", img = "imgs/f11"},
    },
    {
        {name = "01. Saffari Zone", text = "O Saffari Fuchsia zona situa-se no lado esquerdo do Pokemon Center. É um lugar onde você pode encontrar Pokemon raro. Para entrar você deve ter Saffari conta VIP e Pay 1000DL. O diálogo é (hi/Saffari/yes).", img = "imgs/m1"},
        {name = "02. Golden Arena", text = "The Golden Surviva Arena é um evento que ocorre a cada 5 horas, onde os jogadores são teletransportado para uma arena e deve sobreviver várias ondas pokemon e ficando mais forte.\n\nPara participar neste evento você deve ter um Meowth Coin este item pode ser comprado a partir do NPC Meowth Dealer.", img = "imgs/m2"},
        {name = "03. Outland", text = "Outland é um mundo paralelo, onde Pokémon selvagens são muito poderosos. Somente jogadores de nível 150 ou mais pode entrar nesta área. Há 3 portais para Outland: Norte, Sul e Leste .", img = "imgs/m3"}, 
        {name = "04. Pokemon Ditto", text = "Ditto é um Pokémon especial que não tem nenhuma exigência de nível e podem ser comprados na Loja Diamond. a habilidade especial de Ditto é Transform, como faz o ditto ser transformado em outro Pokemon.\n\nDitto para transformar, você deve usar o comando em outro botão Pokemon. No caso de você querer reverter ou processamento, use !revert.", img = "imgs/m4"},
        {name = "05. Boost", text = "Boost é um método de deixar seu Pokémon mais forte. Isso tem que ir para a Máquina Coliseum Boost (Gary`s Lab) <<<\n\nVocê deve colocar a máquina como o impulso de imagem. Pedra < e Pokemon >", img = "imgs/m5"},
        {name = "06. Nome do Pokemon", text = "Pokemon O nome pode ser definido se boost tem uma maior ou igual a 5.\n\nPara alterar o nome, vá até o NPC Shurya em Gary`s Tc Lab ou e dizer o seguinte diálogo:\n\(hi/nick/yes/Nome desejado).", img = "imgs/m6"},
        {name = "07. Aura do Pokemon", text = "Aura é uma animação para quando o Pokemon impulsionar Faixa 50.\n\nIsto pode ser obtido a partir do Tc Lab Gary`s ou o NPC Shurya (Hi/aura/yes/Nombre del aura).", img = "imgs/m7"},
        {name = "08. Addons", text = "Complementos são roupas para Pokemon. Você pode encontrá-los na loja de Diamante ou profissões.", img = "imgs/m8"},
        {name = "09. Held Items", text = "Item Held é um item que um Pokémon pode cobrar . Cada um vem com um recurso chamado de Nível , que nada mais é do que o seu nível é , quanto maior o nível do item Held um será melhor e mais raro.\n\nSeu Pokemon pode carregar apenas dois de cada item Realizada tempo (um com prefixo (X) e um com prefixo (Y). É possível fazer a substituição do item Realizada em seu Pokemon , mais você perde aquele que foi substituído.\n\nLista de Held Item X:\n\nX-Poison    Tier 1 a Tier 7\n\X-Hellfire  Tier 1 a Tier 7\n\X-Attack    Tier 1 a Tier 7\n\X-Return    Tier 1 a Tier 7\n\X-Defense   Tier 1 a Tier 7\n\X-Boost     Tier 1 a Tier 7\n\nLista de Held Item Y:\n\nY-Regeneration Tier 1 a Tier 7\n\Y-Cure         Tier 1 a Tier 7", img = "imgs/m9"},  
    },		
    {
        {name = "01. Torneio", text = "O torneio é realizado no Coliseu, que está localizado à esquerda do centro pokemon de Viridian City. Os torneios acontecem todos os sábados e domingos, a mudança dos padrões cada semana.\n\nHorario: Indefinido.", img = "imgs/g1"},
    },	
    {
        {name = "01. Cofres Perdidos",      text = "Nivel Minimo: 30 a 40.\n\nHá rumores de que existem 4 caixas ao redor do mapa que mantêm grandes recompesas os bravos aventureiros que decidem explorar o mundo Pokémon.\n\nA primeira é na região de Cerulean, a oeste da cidade, protegido por Pokemon venenosa.\n\nA segunda é na região de Pewter, a nordeste de Pokemon Pokemon Center com muitos tipos de pedra, terra e lutador a proteger o tesouro.\n\nA terceira é para o leste da cidade abandonada de Lavender, com vários de tipo Psychic Pokémon protegê-la.\n\nEsta última é a principal ilha de Seafom no porão, junto com vários pokemon tipo de gelo e água.", img = "imgs/t1"},
        {name = "02. Team Rocket y Police", text = "Nivel Minimo: 40.\n\nChico ¿bom ou vilão? É hora de você decidir qual caminho a percorrer no mundo Pokémon. Pense bem, porque essa escolha não voltar para trás e determinar o seu futuro. Numerosas missões e desafios será habilitado após a sua eleição, talvez ainda cedo para concluir, mas não desista, lutar e ganhar a confiança de Giovanni (Foguete/Celadon) ou Capitão Jenny (Polícia/palete).", img = "imgs/t2"},
        {name = "03. Profesor Oak",         text = "Nivel Minimo: 50.\n\nProfessor Oak precisa de você para uma série de tarefas, ajuda para recompensas incríveis.", img = "imgs/t3"},
        {name = "04. Gary Oak",             text = "Nivel Minimo: 50.\n\nGary Carvalho tornou-se um grande pesquisador Pokemon, ajudá-lo em sua busca por boas recompensas.", img = "imgs/t4"},
        {name = "05. Ash Ketchum",          text = "Nivel Minimo: 50.\n\nAsh Ketchum precisa de sua ajuda para investigar a Equipe Rocket e outros mistérios que estão acontecendo no mundo! Mas, primeiro, provar que você é digno de sua confiança.", img = "imgs/t5"},
        {name = "06. Elite Four Bruno",     text = "Nivel Minimo: 60.\n\nBruno é um membro da Elite Four, Os lutadores Pokemon são sua especialidade e sua vida é para treiná-los. Recentemente, Bruno está ajudando uma menina tenta estudar o raro Pokemon Cristal para encontrar inspiração para Mateus, irmão de Marissa.", img = "imgs/t6"},
        {name = "07. Elite Four Lorelei",   text = "Nivel Minimo: 60.\n\nLorelei , um membro da Elite Four, é conhecido por seu estilo lógico e calculando em batalha, gelo Pokemon são sua especialidade. Recentemente, ela procura reforços para vários itens.", img = "imgs/t7"},
        {name = "08. Elite Four Agatha",    text = "Nivel Minimo: 100.\n\nAgatha é uma mulher com um temperamento peculiar e mais um membro da Elite Four. Ela é especializada em fantasma Pokemon, mas sente uma grande curiosidade para qualquer Pokemon com características horripilantes. Sua casa foi enviada para uma outra dimensão das Trevas Abra, alguns espelhos podem abrir a passagem para essa dimensão, encontrá-los e ajudá- Agatha.", img = "imgs/t8"},
        {name = "09. Elite Four Lance",     text = "Nivel Minimo: 80.\n\nLance é o líder da Elite Four, sua especialidade é Pokemon Dragão, é um grande desafio para as técnicas de treinamento e ensino para esse tipo específico de Pokemon Recentemente, ele estava estudando um artefato peculiar: Charizard coração , pouco se sabe sobre esse artefato mas acredita-se que se poderia encontrar em um lugar com muitos Charizard.", img = "imgs/t9"},
        {name = "10. Bill Lighhouse",       text = "Nivel Minimo: 80.\n\nPara resolver o mistério você deve atender a Bill invertigador vivem perto Cerulean. Mais cuidado, isso não é uma maneira fácil, você certamente terá que cumprir vários desafios que talvez não são preparados, boa sorte em sua jornada.", img = "imgs/t10"},
        {name = "11. Cyber World",          text = "Nivel Minimo: 120.\n\nO misterioso Porygon foi sempre um enigma para os pesquisadores Pokemon, mas agora os entrnadores encontrou uma maneira de invadir a World Cyber ?! Para entrar neste mundo você deve falar com o NPC (Shadow), localizado em Tc.", img = "imgs/t11"},
        {name = "12. Batalla Final",        text = "Nivel Minimo: 120.\n\nFinalmente chegou a hora de testar se você é um polícia ou Elite foguete. Polícia invade den/Rocket and Ganale o líder da facção rival. Não se esqueça que este é o último nível desafiar sua equipe tão bem preparado que você pode trazer dois outros treinadores de sua equipe para tentar completar este desafio, boa sorte.", img = "imgs/t12"},
        {name = "13. Pokemon League",       text = "Nivel Minimo: 150.\n\nO Pokemon League, um dos principais objetivos da maioria dos treinadores Pokémon no mundo, é composto por 8 cidades líderes Kanto Ginásio, que lhe dará um emblema para ser derrotado. para obter 8 insígnias, a Elite Four de Kanto se ele pensa que você é elegível para entrar no Pokemon League, onde você vai lutar com o melhor! Tente derrotá-los é capaz de ser o vencedor da Liga Pokémon.", img = "imgs/t13"},
        {name = "14. Outland Main",         text = "Nivel Minimo: 150.\n\nPokemon forte e agressivo de uma outra dimensão chamado Outland estão tentando invadir o nosso mundo, Líderes de Ginásio e treinadores lendários estão lá, o que você espera de ajuda? Fale com Ash Ketchum em Outland East.", img = "imgs/t14"},
        {name = "15. Duelist Brotherhood",  text = "Nivel Minimo: 150.\n\nDuelists Liga Pokemon treinadores estão à procura forte o suficiente para encontrar Hunters, NPCs High End Você deve derrotar ( 10.000) NPCs sem limite por dia.", img = "imgs/t15"},
        {name = "16. Moro Island",          text = "Nivel Minimo: 100.\n\nA principal atração Museu de Arte de Moro Island, um troféu 300 Anos de Orange Island, foi roubado. Suspeita-se da Equipe Rocket, mas não tenho certeza. Você ajuda o caso oficial Jenny deve corrigir isso?", img = "imgs/t16"},
        {name = "17. Golden Island",        text = "Nivel Minimo: 100.\n\nO Meowth Tribo da recompensa está em busca de um treinador Pokemon que pode ajudá -los a realizar sua profecia, há boatos de que esta tribo muitas vezes recompensar pessoas de fora que ajudam muito bem!.", img = "imgs/t17"},
        {name = "18. Tarroco Island",       text = "Nivel Minimo: 60.\n\nPolícia Capitão Locker Tarroco Island e está na necessidade de forte polícia por ataque com foguete na base.", img = "imgs/t18"},
        {name = "19. Ascorbia Island",      text = "Nivel Minimo: 60.\n\nButch e Cassidy estão em Ascorbia Island, planejando um ataque contra a polícia, ajuda se você é de Rocket!.", img = "imgs/t19"},
        {name = "20. Firefighter",          text = "Nivel Minimo: 30.\n\nCorpo de bombeiros de trem, surpreendeu ao capitão Aiden e sua água Pokemon para obter uma recompensa única.", img = "imgs/t20"},
        {name = "21. Grapefruit Island",    text = "Nivel Minimo: 60.\n\nA Snorlax gigante está comendo grapefruit todas as ilhas Toranja Sete, ajudar o pesquisador Pokémon Ruby e seus colegas biólogos para prevenir todas as frutas são comidos!.", img = "imgs/t21"},
        {name = "22. Kabuto Fossil Island", text = "Nivel Minimo: 100.\n\nHá Alguém sabotar expedição científica de volta para Kabuto Fóssil encontra essa pessoa e ajudar a Enfermeira Joy para resolver o mistério por trás dessa ilha!.", img = "imgs/t25"},
        {name = "23. Pikan Island",         text = "Nivel Minimo: 60.\n\nPikan fruta é uma fruta comum nesta ilha, Pokemon que consumiram este fruto por um longo tempo se tornou Rosa Pokemon. Equipe Rocket está tentando roubar esses Pokemon, se você é de Rocket você terá que ajudar se a polícia tentar detê-los!.", img = "imgs/t22"},
        {name = "24. Mew Island",           text = "Nivel Minimo: 120.\n\nMewtwo está tentando dominar o mundo com seu exército de clones, tentar pará-lo antes que seja tarde demais. Esta missão pode ser repetido a cada mês.", img = "imgs/t23"},
        {name = "25. Mewtwo Final",         text = "Nivel Minimo: 200.\n\nMewtwo convidar apenas os melhores treinadores do mundo para testemunhar seu poder e superioridade do Pokemon que você pode obter clones.¿Sera parar?.", img = "imgs/t24"},
    },	    
}